#pragma once

namespace TCLAP {

/**
 * A base class that defines the interface for visitors.
 */
class Visitor
{
	public:

		/**
		 * Constructor. Does nothing.
		 */
		Visitor() = default;

		/**
		 * Destructor. Does nothing.
		 */
		virtual ~Visitor() = default;

		/**
		 * Does nothing. Should be overridden by child.
		 */
		virtual void visit() { }
};

}

